import React from "react";
import { useRef } from "react";
import { useEffect } from "react";
import { useState } from "react";
import "../css/projects.css"

const ShowPage = () =>{

    const [data, SetData] = useState([]);

    let k = 0;
    const media = useRef()

    useEffect(() => {
        fetch("Projects.json")
            .then(res => res.json())
            .then(json => {
                SetData(json);
            });
    }, [])
    return(
    <div>
        <table>
            <tbody>
                {
                    data.map(project => {
                        k++;
                        return(
                            <tr key={k}>
                                <td className="project-title">{project.Title}</td>
                                <td className="project-anchor"><a href={project.Link}> Click Here</a></td>
                                <td className="project-img"><img ref={`${media}/${project.Img}`} /></td>
                            </tr>
                        )
                    })
                }
            </tbody>
        </table>
    </div>
    )
}

export default ShowPage;
